import React from 'react'
import styled from 'styled-components'
import ArrowLeftOutlinedIcon from '@mui/icons-material/ArrowLeftOutlined';
import ArrowRightOutlinedIcon from '@mui/icons-material/ArrowRightOutlined';

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  //background-color: coral;
  position: reletive;
`
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7f;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"};
  right: ${props=> props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`

const Wrapper = styled.div`
 height: 100px;
`

const Slide = styled.div`
  width: 100vw;
  height:100vh;
  display: flex;
  align-items: center;
`

const Image = styled.img`
  height: 80%;
  wight:100%
`

const ImgContainer = styled.div`
  height:100%;
  flex: 1;
  padding: 50px;
`

const Title = styled.h1`
  font-sixe: 70px;`
const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`

const Button = styled.button``

const InfoContainer = styled.div`
flex: 1;
`


const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
         <ArrowLeftOutlinedIcon/>
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Image src="https://user-images.githubusercontent.com/75200810/235105280-90f8739c-6e13-4c46-97ec-0f552f3b225a.png"/>
          </ImgContainer>
          <InfoContainer>
            <Title>Summer sale</Title>
            <Desc>DON'T COMPROMISE ON STYLE! SET FLAT 30% OFF FOR NEW ARRIVALS.</Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
      </Wrapper>  
      <Arrow  direction="right">
         <ArrowRightOutlinedIcon/>
      </Arrow>
    </Container>
  )
}

export default Slider
